import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./UploadResume.css";

const UploadResume = () => {
  const [file, setFile] = useState(null);
  const [isDragging, setIsDragging] = useState(false);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleDragOver = (e) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = (e) => {
    e.preventDefault();
    setIsDragging(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setIsDragging(false);
    const droppedFile = e.dataTransfer.files[0];
    handleFile(droppedFile);
  };

  const handleFileInput = (e) => {
    const selectedFile = e.target.files[0];
    handleFile(selectedFile);
  };

  const handleFile = (file) => {
    if (file) {
      if (
        file.type === "application/pdf" ||
        file.type === "application/msword" ||
        file.type ===
          "application/vnd.openxmlformats-officedocument.wordprocessingml.document"
      ) {
        setFile(file);
        setError("");
      } else {
        setError("Please upload a PDF or Word document");
      }
    }
  };

  const handleUpload = async () => {
    if (!file) {
      setError("Please select a file first");
      return;
    }

    // Generate random ATS score between 65 and 95
    const atsScore = Math.floor(Math.random() * (95 - 65 + 1)) + 65;

    const dummyData = {
      personalInfo: {
        fullName: "Sample User",
        title: "Professional",
      },
      contact: {
        email: "sample@email.com",
        phone: "123-456-7890",
        location: "City, State",
      },
      professionalSummary: "Experienced professional with a strong background.",
      skills: ["Skill 1", "Skill 2", "Skill 3"],
      workExperience: [
        {
          company: "Company Name",
          position: "Position",
          duration: "2020 - Present",
        },
      ],
      education: [
        {
          degree: "Bachelor's Degree",
          institution: "University Name",
          year: "2020",
        },
      ],
      atsScore: atsScore,
    };

    navigate("/resume-preview", { state: { resumeData: dummyData } });
  };

  return (
    <div className="upload-resume-container">
      <div className="upload-resume-content">
        <h1>Upload Your Resume</h1>
        <p className="upload-description">
          Upload your existing resume and we'll help you analyze it and give you
          a ATS score.
        </p>

        <div
          className={`upload-area ${isDragging ? "dragging" : ""} ${
            file ? "has-file" : ""
          }`}
          onDragOver={handleDragOver}
          onDragLeave={handleDragLeave}
          onDrop={handleDrop}
        >
          <input
            type="file"
            id="file-input"
            accept=".pdf,.doc,.docx"
            onChange={handleFileInput}
            className="file-input"
          />
          <div className="upload-icon">
            <svg
              viewBox="0 0 24 24"
              fill="none"
              stroke="currentColor"
              strokeWidth="2"
            >
              <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4" />
              <polyline points="17 8 12 3 7 8" />
              <line x1="12" y1="3" x2="12" y2="15" />
            </svg>
          </div>
          <p className="upload-text">
            {file
              ? file.name
              : "Drag & drop your resume here or click to browse"}
          </p>
          <p className="upload-hint">Supported formats: PDF, DOC, DOCX</p>
        </div>

        {error && <p className="error-message">{error}</p>}

        <div className="upload-actions">
          <button
            className="upload-button"
            onClick={handleUpload}
            disabled={!file}
          >
            Upload Resume
          </button>
          <button className="back-button" onClick={() => navigate(-1)}>
            Go Back
          </button>
        </div>
      </div>
    </div>
  );
};

export default UploadResume;
