import React, { useState, useRef, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./Chat.css";

const Chat = () => {
  const [messages, setMessages] = useState([
    {
      type: "bot",
      text: "Hello! I'll help you create your ATS-friendly resume. What is your full name?",
    },
  ]);
  const [input, setInput] = useState("");
  const [currentStep, setCurrentStep] = useState("name");
  const [isComplete, setIsComplete] = useState(false);
  const [userData, setUserData] = useState({});
  const messagesEndRef = useRef(null);
  const navigate = useNavigate();

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleInputChange = (e) => {
    setInput(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    // Add user message
    setMessages((prev) => [...prev, { type: "user", text: input }]);

    try {
      const response = await fetch("http://localhost:3001/api/chat", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          message: input,
          step: currentStep,
        }),
      });

      const data = await response.json();

      if (data.error) {
        setMessages((prev) => [
          ...prev,
          {
            type: "bot",
            text: "Sorry, something went wrong. Please try again.",
          },
        ]);
        return;
      }

      // Store user data
      const newUserData = { ...userData };
      switch (currentStep) {
        case "name":
          newUserData.personalInfo = { ...newUserData.personalInfo, name: input };
          break;
        case "email":
          newUserData.personalInfo = { ...newUserData.personalInfo, email: input };
          break;
        case "phone":
          newUserData.personalInfo = { ...newUserData.personalInfo, phone: input };
          break;
        case "location":
          newUserData.personalInfo = { ...newUserData.personalInfo, location: input };
          break;
        case "linkedin":
          newUserData.personalInfo = { ...newUserData.personalInfo, linkedin: input };
          break;
        case "portfolio":
          newUserData.personalInfo = { ...newUserData.personalInfo, portfolio: input };
          break;
        case "summary":
          newUserData.summary = input;
          break;
        case "technicalSkills":
          newUserData.skills = { ...newUserData.skills, technical: input.split(",").map(skill => skill.trim()) };
          break;
        case "softSkills":
          newUserData.skills = { ...newUserData.skills, soft: input.split(",").map(skill => skill.trim()) };
          break;
        case "jobTitle":
          newUserData.currentExperience = { title: input };
          break;
        case "company":
          newUserData.currentExperience = { ...newUserData.currentExperience, company: input };
          break;
        case "jobLocation":
          newUserData.currentExperience = { ...newUserData.currentExperience, location: input };
          break;
        case "jobStartDate":
          newUserData.currentExperience = { ...newUserData.currentExperience, startDate: input };
          break;
        case "jobEndDate":
          newUserData.currentExperience = { ...newUserData.currentExperience, endDate: input };
          break;
        case "jobResponsibilities":
          newUserData.currentExperience = { 
            ...newUserData.currentExperience, 
            responsibilities: input.split("\n").filter(line => line.trim()) 
          };
          newUserData.experience = [...(newUserData.experience || []), newUserData.currentExperience];
          delete newUserData.currentExperience;
          break;
        case "degree":
          newUserData.currentEducation = { degree: input };
          break;
        case "university":
          newUserData.currentEducation = { ...newUserData.currentEducation, university: input };
          break;
        case "educationLocation":
          newUserData.currentEducation = { ...newUserData.currentEducation, location: input };
          break;
        case "educationStartDate":
          newUserData.currentEducation = { ...newUserData.currentEducation, startDate: input };
          break;
        case "educationEndDate":
          newUserData.currentEducation = { ...newUserData.currentEducation, endDate: input };
          break;
        case "educationScore":
          newUserData.currentEducation = { ...newUserData.currentEducation, score: input };
          newUserData.education = [...(newUserData.education || []), newUserData.currentEducation];
          delete newUserData.currentEducation;
          break;
        case "projectTitle":
          newUserData.currentProject = { title: input };
          break;
        case "projectTechnologies":
          newUserData.currentProject = { ...newUserData.currentProject, technologies: input.split(",").map(tech => tech.trim()) };
          break;
        case "projectDescription":
          newUserData.currentProject = { ...newUserData.currentProject, description: input };
          break;
        case "projectUrl":
          newUserData.currentProject = { ...newUserData.currentProject, url: input };
          newUserData.projects = [...(newUserData.projects || []), newUserData.currentProject];
          delete newUserData.currentProject;
          break;
        case "certificateName":
          newUserData.currentCertification = { name: input };
          break;
        case "certificateIssuer":
          newUserData.currentCertification = { ...newUserData.currentCertification, issuer: input };
          break;
        case "certificateDate":
          newUserData.currentCertification = { ...newUserData.currentCertification, date: input };
          newUserData.certifications = [...(newUserData.certifications || []), newUserData.currentCertification];
          delete newUserData.currentCertification;
          break;
        case "awardTitle":
          newUserData.currentAward = { title: input };
          break;
        case "awardIssuer":
          newUserData.currentAward = { ...newUserData.currentAward, issuer: input };
          break;
        case "awardYear":
          newUserData.currentAward = { ...newUserData.currentAward, year: input };
          break;
        case "awardDescription":
          newUserData.currentAward = { ...newUserData.currentAward, description: input };
          newUserData.awards = [...(newUserData.awards || []), newUserData.currentAward];
          delete newUserData.currentAward;
          break;
        case "languageName":
          newUserData.currentLanguage = { name: input };
          break;
        case "languageProficiency":
          newUserData.currentLanguage = { ...newUserData.currentLanguage, proficiency: input };
          newUserData.languages = [...(newUserData.languages || []), newUserData.currentLanguage];
          delete newUserData.currentLanguage;
          break;
        default:
          console.log("Unknown step:", currentStep);
          break;
      }
      setUserData(newUserData);

      // Add bot response
      setMessages((prev) => [
        ...prev,
        { type: "bot", text: data.nextQuestion },
      ]);

      // Update step
      if (data.isComplete) {
        setIsComplete(true);
        // Navigate to preview page with user data
        navigate("/resume-preview", { state: { resumeData: newUserData } });
      } else {
        // Handle yes/no responses for adding more items
        if (input.toLowerCase() === "yes" || input.toLowerCase() === "no") {
          switch (currentStep) {
            case "addMoreExperience":
              setCurrentStep(input.toLowerCase() === "yes" ? "jobTitle" : "degree");
              break;
            case "addMoreEducation":
              setCurrentStep(input.toLowerCase() === "yes" ? "degree" : "projectTitle");
              break;
            case "addMoreProject":
              setCurrentStep(input.toLowerCase() === "yes" ? "projectTitle" : "certificateName");
              break;
            case "addMoreCertification":
              setCurrentStep(input.toLowerCase() === "yes" ? "certificateName" : "awardTitle");
              break;
            case "addMoreAward":
              setCurrentStep(input.toLowerCase() === "yes" ? "awardTitle" : "languageName");
              break;
            case "addMoreLanguage":
              setCurrentStep(input.toLowerCase() === "yes" ? "languageName" : "complete");
              break;
            default:
              setCurrentStep(data.nextStep || currentStep);
          }
        } else {
          // Handle regular steps
          switch (currentStep) {
            case "name":
              setCurrentStep("email");
              break;
            case "email":
              setCurrentStep("phone");
              break;
            case "phone":
              setCurrentStep("location");
              break;
            case "location":
              setCurrentStep("linkedin");
              break;
            case "linkedin":
              setCurrentStep("portfolio");
              break;
            case "portfolio":
              setCurrentStep("summary");
              break;
            case "summary":
              setCurrentStep("technicalSkills");
              break;
            case "technicalSkills":
              setCurrentStep("softSkills");
              break;
            case "softSkills":
              setCurrentStep("jobTitle");
              break;
            case "jobTitle":
              setCurrentStep("company");
              break;
            case "company":
              setCurrentStep("jobLocation");
              break;
            case "jobLocation":
              setCurrentStep("jobStartDate");
              break;
            case "jobStartDate":
              setCurrentStep("jobEndDate");
              break;
            case "jobEndDate":
              setCurrentStep("jobResponsibilities");
              break;
            case "jobResponsibilities":
              setCurrentStep("addMoreExperience");
              break;
            case "degree":
              setCurrentStep("university");
              break;
            case "university":
              setCurrentStep("educationLocation");
              break;
            case "educationLocation":
              setCurrentStep("educationStartDate");
              break;
            case "educationStartDate":
              setCurrentStep("educationEndDate");
              break;
            case "educationEndDate":
              setCurrentStep("educationScore");
              break;
            case "educationScore":
              setCurrentStep("addMoreEducation");
              break;
            case "projectTitle":
              setCurrentStep("projectTechnologies");
              break;
            case "projectTechnologies":
              setCurrentStep("projectDescription");
              break;
            case "projectDescription":
              setCurrentStep("projectUrl");
              break;
            case "projectUrl":
              setCurrentStep("addMoreProject");
              break;
            case "certificateName":
              setCurrentStep("certificateIssuer");
              break;
            case "certificateIssuer":
              setCurrentStep("certificateDate");
              break;
            case "certificateDate":
              setCurrentStep("addMoreCertification");
              break;
            case "awardTitle":
              setCurrentStep("awardIssuer");
              break;
            case "awardIssuer":
              setCurrentStep("awardYear");
              break;
            case "awardYear":
              setCurrentStep("awardDescription");
              break;
            case "awardDescription":
              setCurrentStep("addMoreAward");
              break;
            case "languageName":
              setCurrentStep("languageProficiency");
              break;
            case "languageProficiency":
              setCurrentStep("addMoreLanguage");
              break;
            default:
              setCurrentStep(data.nextStep || currentStep);
          }
        }
      }

      setInput("");
    } catch (error) {
      console.error("Error:", error);
      setMessages((prev) => [
        ...prev,
        { type: "bot", text: "Sorry, something went wrong. Please try again." },
      ]);
    }
  };

  const handleGenerateResume = async () => {
    try {
      const response = await fetch(
        "http://localhost:3001/api/generate-resume",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(userData),
        }
      );

      if (response.ok) {
        const blob = await response.blob();
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement("a");
        a.href = url;
        a.download = `${userData.personalInfo.name.replace(/\s+/g, "_")}_Resume.pdf`;
        document.body.appendChild(a);
        a.click();
        window.URL.revokeObjectURL(url);
        document.body.removeChild(a);

        // Show ATS prompt after resume generation
        setMessages((prev) => [
          ...prev,
          {
            type: "bot",
            text: "Would you like to check how well your resume matches a specific job description? Paste the job description below.",
          },
        ]);
      } else {
        setMessages((prev) => [
          ...prev,
          {
            type: "bot",
            text: "Sorry, there was an error generating your resume.",
          },
        ]);
      }
    } catch (error) {
      console.error("Error:", error);
      setMessages((prev) => [
        ...prev,
        {
          type: "bot",
          text: "Sorry, there was an error generating your resume.",
        },
      ]);
    }
  };

  return (
    <div className="chat-container">
      <div className="messages">
        {messages.map((message, index) => (
          <div key={index} className={`message ${message.type}`}>
            <div className="message-content">{message.text}</div>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>
      {isComplete ? (
        <div className="button-container">
          <button className="generate-button" onClick={handleGenerateResume}>
            Generate Resume
          </button>
        </div>
      ) : (
        <form onSubmit={handleSubmit} className="input-form">
          <div className="input-container">
            <input
              type="text"
              value={input}
              onChange={handleInputChange}
              placeholder="Type your answer..."
              className="chat-input"
            />
          </div>
          <button type="submit" className="send-button">
            Send
          </button>
        </form>
      )}
    </div>
  );
};

export default Chat;
