import Typo from "typo-js";

// Initialize the spell checker with English dictionary
const dictionary = new Typo("en_US");

export const checkSpelling = (text) => {
  if (!text) return { hasErrors: false, suggestions: [] };

  const words = text.split(/\s+/);
  const errors = [];

  words.forEach((word) => {
    // Remove punctuation and convert to lowercase for checking
    const cleanWord = word
      .replace(/[.,/#!$%^&*;:{}=\-_`~()]/g, "")
      .toLowerCase();

    // Skip empty strings and numbers
    if (!cleanWord || /^\d+$/.test(cleanWord)) return;

    if (!dictionary.check(cleanWord)) {
      const suggestions = dictionary.suggest(cleanWord);
      errors.push({
        word,
        suggestions: suggestions.slice(0, 3), // Get top 3 suggestions
      });
    }
  });

  return {
    hasErrors: errors.length > 0,
    errors,
  };
};

export const getHighlightedText = (text, errors, onWordClick) => {
  if (!errors || errors.length === 0) return text;

  let highlightedText = text;
  errors.forEach(({ word }) => {
    const regex = new RegExp(`\\b${word}\\b`, "gi");
    highlightedText = highlightedText.replace(
      regex,
      `<span class="spelling-error" onclick="window.handleWordClick('${word}')">${word}</span>`
    );
  });

  return highlightedText;
};
