import React, { useState, useRef, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { checkSpelling, getHighlightedText } from "../utils/spellChecker";
import "./Chat.css";

const Chat = () => {
  const [messages, setMessages] = useState([
    {
      type: "bot",
      text: "Hello! I'll help you create your resume. What is your full name?",
    },
  ]);
  const [input, setInput] = useState("");
  const [currentStep, setCurrentStep] = useState("name");
  const [isComplete, setIsComplete] = useState(false);
  const [showAtsPrompt, setShowAtsPrompt] = useState(false);
  const [atsScore, setAtsScore] = useState(null);
  const [userData, setUserData] = useState({});
  const [spellErrors, setSpellErrors] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);
  const [selectedWord, setSelectedWord] = useState(null);
  const messagesEndRef = useRef(null);
  const navigate = useNavigate();

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleInputChange = (e) => {
    const newInput = e.target.value;
    setInput(newInput);

    // Check spelling as user types
    const { errors } = checkSpelling(newInput);
    setSpellErrors(errors);
  };

  const handleWordClick = (word) => {
    const error = spellErrors.find((err) => err.word === word);
    if (error) {
      setSelectedWord({ word, suggestions: error.suggestions });
      setShowSuggestions(true);
    }
  };

  const handleSuggestionClick = (suggestion) => {
    const newInput = input.replace(
      new RegExp(`\\b${selectedWord.word}\\b`, "gi"),
      suggestion
    );
    setInput(newInput);
    setShowSuggestions(false);
    setSelectedWord(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    // Add user message
    setMessages((prev) => [...prev, { type: "user", text: input }]);

    try {
      const response = await fetch("http://localhost:3001/api/chat", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          message: input,
          step: currentStep,
        }),
      });

      const data = await response.json();

      if (data.error) {
        setMessages((prev) => [
          ...prev,
          {
            type: "bot",
            text: "Sorry, something went wrong. Please try again.",
          },
        ]);
        return;
      }

      // Store user data
      const newUserData = { ...userData };
      switch (currentStep) {
        case "name":
          newUserData.name = input;
          break;
        case "email":
          newUserData.email = input;
          break;
        case "skills":
          newUserData.skills = input.split(",").map((skill) => skill.trim());
          break;
        case "experience":
          newUserData.experience = input;
          break;
        case "education":
          newUserData.education = input;
          break;
        default:
          console.log("Unknown step:", currentStep);
          break;
      }
      setUserData(newUserData);

      // Add bot response
      setMessages((prev) => [
        ...prev,
        { type: "bot", text: data.nextQuestion },
      ]);

      // Update step
      if (data.isComplete) {
        setIsComplete(true);
        // Navigate to preview page with user data
        navigate("/resume-preview", { state: { resumeData: newUserData } });
      } else {
        const steps = ["name", "email", "skills", "experience", "education"];
        const currentIndex = steps.indexOf(currentStep);
        setCurrentStep(steps[currentIndex + 1]);
      }

      setInput("");
      setSpellErrors([]);
    } catch (error) {
      console.error("Error:", error);
      setMessages((prev) => [
        ...prev,
        { type: "bot", text: "Sorry, something went wrong. Please try again." },
      ]);
    }
  };

  const handleGenerateResume = async () => {
    try {
      const response = await fetch(
        "http://localhost:3001/api/generate-resume",
        {
          method: "POST",
        }
      );

      if (response.ok) {
        const blob = await response.blob();
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement("a");
        a.href = url;
        a.download = "resume.pdf";
        document.body.appendChild(a);
        a.click();
        window.URL.revokeObjectURL(url);
        document.body.removeChild(a);

        // Show ATS prompt after resume generation
        setShowAtsPrompt(true);
        setMessages((prev) => [
          ...prev,
          {
            type: "bot",
            text: "Would you like to check how well your resume matches a specific job description? Paste the job description below.",
          },
        ]);
      } else {
        setMessages((prev) => [
          ...prev,
          {
            type: "bot",
            text: "Sorry, there was an error generating your resume.",
          },
        ]);
      }
    } catch (error) {
      console.error("Error:", error);
      setMessages((prev) => [
        ...prev,
        {
          type: "bot",
          text: "Sorry, there was an error generating your resume.",
        },
      ]);
    }
  };

  const handleAtsScore = async (jobDescription) => {
    try {
      const response = await fetch("http://localhost:3001/api/score-resume", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ jobDescription }),
      });

      const data = await response.json();

      if (response.ok) {
        setAtsScore(data);
        setMessages((prev) => [
          ...prev,
          {
            type: "bot",
            text: `Your resume scored ${
              data.score
            }% for this job!\n\nKeyword Match: ${
              data.details.keywordMatch
            }%\nTitle Match: ${
              data.details.titleMatch
            }%\n\nMatching Keywords: ${data.details.matchingKeywords.join(
              ", "
            )}`,
          },
        ]);
      } else {
        setMessages((prev) => [
          ...prev,
          {
            type: "bot",
            text: "Sorry, there was an error scoring your resume.",
          },
        ]);
      }
    } catch (error) {
      console.error("Error:", error);
      setMessages((prev) => [
        ...prev,
        {
          type: "bot",
          text: "Sorry, there was an error scoring your resume.",
        },
      ]);
    }
  };

  return (
    <div className="chat-container">
      <div className="messages">
        {messages.map((message, index) => (
          <div key={index} className={`message ${message.type}`}>
            <div
              className="message-content"
              dangerouslySetInnerHTML={{
                __html:
                  message.type === "user"
                    ? getHighlightedText(
                        message.text,
                        spellErrors,
                        handleWordClick
                      )
                    : message.text,
              }}
            />
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>
      {isComplete ? (
        <div className="button-container">
          <button className="generate-button" onClick={handleGenerateResume}>
            Generate Resume
          </button>
        </div>
      ) : (
        <form onSubmit={handleSubmit} className="input-form">
          <div className="input-container">
            <input
              type="text"
              value={input}
              onChange={handleInputChange}
              placeholder="Type your answer..."
              className="chat-input"
            />
            {showSuggestions && selectedWord && (
              <div className="suggestions-popup">
                {selectedWord.suggestions.map((suggestion, index) => (
                  <div
                    key={index}
                    className="suggestion-item"
                    onClick={() => handleSuggestionClick(suggestion)}
                  >
                    {suggestion}
                  </div>
                ))}
              </div>
            )}
          </div>
          <button type="submit" className="send-button">
            Send
          </button>
        </form>
      )}
      {showAtsPrompt && !atsScore && (
        <form
          onSubmit={(e) => {
            e.preventDefault();
            handleAtsScore(input);
            setInput("");
          }}
          className="input-form"
        >
          <textarea
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Paste the job description here..."
            className="chat-input"
            rows={4}
          />
          <button type="submit" className="send-button">
            Score Resume
          </button>
        </form>
      )}
    </div>
  );
};

export default Chat;
