import React, { useState, useRef, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./Chat.css";

const Chat = () => {
  const [messages, setMessages] = useState([
    {
      type: "bot",
      text: "Hello! I'll help you create your resume. What is your full name?",
    },
  ]);
  const [input, setInput] = useState("");
  const [currentStep, setCurrentStep] = useState("name");
  const [isComplete, setIsComplete] = useState(false);
  const [userData, setUserData] = useState({});
  const [spellingCorrection, setSpellingCorrection] = useState(null);
  const messagesEndRef = useRef(null);
  const navigate = useNavigate();

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleInputChange = (e) => {
    setInput(e.target.value);
  };

  const handleSpellingCorrection = (useCorrected) => {
    if (useCorrected && spellingCorrection) {
      setInput(spellingCorrection.correctedText);
      setUserData((prev) => ({
        ...prev,
        [currentStep]: spellingCorrection.correctedText,
      }));
    }
    setSpellingCorrection(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    // Add user message
    setMessages((prev) => [...prev, { type: "user", text: input }]);

    try {
      const response = await fetch("http://localhost:3001/api/chat", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          message: input,
          step: currentStep,
        }),
      });

      const data = await response.json();

      if (data.error) {
        setMessages((prev) => [
          ...prev,
          {
            type: "bot",
            text: "Sorry, something went wrong. Please try again.",
          },
        ]);
        return;
      }

      // Handle spelling correction if needed
      if (data.hasSpellingIssues) {
        setSpellingCorrection({
          originalText: data.originalText,
          correctedText: data.correctedText,
          issues: data.issues,
        });
        setMessages((prev) => [
          ...prev,
          {
            type: "bot",
            text: data.nextQuestion,
            hasSpellingIssues: true,
          },
        ]);
        return;
      }

      // Store user data
      const newUserData = { ...userData };
      switch (currentStep) {
        case "name":
          newUserData.name = input;
          break;
        case "email":
          newUserData.email = input;
          break;
        case "skills":
          newUserData.skills = input.split(",").map((skill) => skill.trim());
          break;
        case "experience":
          newUserData.experience = input;
          break;
        case "education":
          newUserData.education = input;
          break;
        default:
          console.log("Unknown step:", currentStep);
          break;
      }
      setUserData(newUserData);

      // Add bot response
      setMessages((prev) => [
        ...prev,
        { type: "bot", text: data.nextQuestion },
      ]);

      // Update step
      if (data.isComplete) {
        setIsComplete(true);
        // Navigate to preview page with user data
        navigate("/resume-preview", { state: { resumeData: newUserData } });
      } else {
        const steps = ["name", "email", "skills", "experience", "education"];
        const currentIndex = steps.indexOf(currentStep);
        setCurrentStep(steps[currentIndex + 1]);
      }

      setInput("");
    } catch (error) {
      console.error("Error:", error);
      setMessages((prev) => [
        ...prev,
        { type: "bot", text: "Sorry, something went wrong. Please try again." },
      ]);
    }
  };

  const handleGenerateResume = async () => {
    try {
      const response = await fetch(
        "http://localhost:3001/api/generate-resume",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(userData),
        }
      );

      if (response.ok) {
        const blob = await response.blob();
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement("a");
        a.href = url;
        a.download = `${userData.name.replace(/\s+/g, "_")}_Resume.pdf`;
        document.body.appendChild(a);
        a.click();
        window.URL.revokeObjectURL(url);
        document.body.removeChild(a);

        // Show ATS prompt after resume generation
        setMessages((prev) => [
          ...prev,
          {
            type: "bot",
            text: "Would you like to check how well your resume matches a specific job description? Paste the job description below.",
          },
        ]);
        setCurrentStep("ats");
      } else {
        setMessages((prev) => [
          ...prev,
          {
            type: "bot",
            text: "Sorry, there was an error generating your resume.",
          },
        ]);
      }
    } catch (error) {
      console.error("Error:", error);
      setMessages((prev) => [
        ...prev,
        {
          type: "bot",
          text: "Sorry, there was an error generating your resume.",
        },
      ]);
    }
  };

  return (
    <div className="chat-container">
      <div className="messages">
        {messages.map((message, index) => (
          <div key={index} className={`message ${message.type}`}>
            <div className="message-content">{message.text}</div>
            {message.hasSpellingIssues && spellingCorrection && (
              <div className="spelling-actions">
                <button
                  onClick={() => handleSpellingCorrection(true)}
                  className="accept-correction"
                >
                  Accept Correction
                </button>
                <button
                  onClick={() => handleSpellingCorrection(false)}
                  className="keep-original"
                >
                  Keep Original
                </button>
              </div>
            )}
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>
      {isComplete ? (
        <div className="button-container">
          <button className="generate-button" onClick={handleGenerateResume}>
            Generate Resume
          </button>
        </div>
      ) : (
        <form onSubmit={handleSubmit} className="input-form">
          <div className="input-container">
            <input
              type="text"
              value={input}
              onChange={handleInputChange}
              placeholder="Type your answer..."
              className="chat-input"
            />
          </div>
          <button type="submit" className="send-button">
            Send
          </button>
        </form>
      )}
    </div>
  );
};

export default Chat;
