import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./UploadResume.css";

const UploadResume = () => {
  const [file, setFile] = useState(null);
  const [isDragging, setIsDragging] = useState(false);
  const [error, setError] = useState("");
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [atsScore, setAtsScore] = useState(null);
  const navigate = useNavigate();

  const handleDragOver = (e) => {
    e.preventDefault();
    setIsDragging(true);
  };

  const handleDragLeave = (e) => {
    e.preventDefault();
    setIsDragging(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setIsDragging(false);
    const droppedFile = e.dataTransfer.files[0];
    handleFile(droppedFile);
  };

  const handleFileInput = (e) => {
    const selectedFile = e.target.files[0];
    handleFile(selectedFile);
  };

  const handleFile = (file) => {
    if (file) {
      if (
        file.type === "application/pdf" ||
        file.type === "application/msword" ||
        file.type ===
          "application/vnd.openxmlformats-officedocument.wordprocessingml.document"
      ) {
        setFile(file);
        setError("");
        setAtsScore(null);
      } else {
        setError("Please upload a PDF or Word document");
      }
    }
  };

  const generateRandomScore = () => {
    return Math.floor(Math.random() * (100 - 50 + 1)) + 50;
  };

  const handleUpload = async () => {
    if (!file) {
      setError("Please select a file first");
      return;
    }

    try {
      setIsAnalyzing(true);
      setError("");

      // Simulate API call delay
      await new Promise(resolve => setTimeout(resolve, 2000));

      // Generate random ATS score
      const score = generateRandomScore();
      setAtsScore(score);

      const formData = new FormData();
      formData.append("resume", file);

      const response = await fetch("http://localhost:3001/api/upload-resume", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        throw new Error("Upload failed");
      }

      const data = await response.json();
      // Navigate to the resume preview page with the extracted data
      navigate("/resume-preview", { state: { resumeData: data } });
    } catch (err) {
      setError("Failed to upload resume. Please try again.");
    } finally {
      setIsAnalyzing(false);
    }
  };

  const getScoreColor = (score) => {
    if (score >= 90) return "#4caf50";
    if (score >= 75) return "#2196f3";
    if (score >= 60) return "#ff9800";
    return "#f44336";
  };

  return (
    <div className="upload-resume-container">
      <div className="upload-resume-content">
        <h1>Upload Your Resume</h1>
        <p className="upload-description">
          Upload your existing resume and we'll help you create a professional
          version
        </p>

        <div
          className={`upload-area ${isDragging ? "dragging" : ""} ${
            file ? "has-file" : ""
          }`}
          onDragOver={handleDragOver}
          onDragLeave={handleDragLeave}
          onDrop={handleDrop}
        >
          <input
            type="file"
            id="file-input"
            accept=".pdf,.doc,.docx"
            onChange={handleFileInput}
            className="file-input"
          />
          <div className="upload-icon">
            <svg
              viewBox="0 0 24 24"
              fill="none"
              stroke="currentColor"
              strokeWidth="2"
            >
              <path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4" />
              <polyline points="17 8 12 3 7 8" />
              <line x1="12" y1="3" x2="12" y2="15" />
            </svg>
          </div>
          <p className="upload-text">
            {file
              ? file.name
              : "Drag & drop your resume here or click to browse"}
          </p>
          <p className="upload-hint">Supported formats: PDF, DOC, DOCX</p>
        </div>

        {error && <p className="error-message">{error}</p>}

        {atsScore && (
          <div className="ats-score-container">
            <h3>ATS Score Analysis</h3>
            <div 
              className="ats-score-circle"
              style={{ 
                background: `conic-gradient(${getScoreColor(atsScore)} ${atsScore * 3.6}deg, #f0f0f0 0deg)`
              }}
            >
              <div className="ats-score-inner">
                <span className="ats-score-value">{atsScore}%</span>
                <span className="ats-score-label">ATS Score</span>
              </div>
            </div>
            <p className="ats-score-message">
              {atsScore >= 90
                ? "Excellent! Your resume is well-optimized for ATS systems."
                : atsScore >= 75
                ? "Good! Your resume has decent ATS compatibility."
                : atsScore >= 60
                ? "Fair. Consider improving your resume's ATS compatibility."
                : "Needs improvement. Your resume may not perform well in ATS systems."}
            </p>
          </div>
        )}

        <div className="upload-actions">
          <button
            className="upload-button"
            onClick={handleUpload}
            disabled={!file || isAnalyzing}
          >
            {isAnalyzing ? "Analyzing..." : "Upload Resume"}
          </button>
          <button className="back-button" onClick={() => navigate(-1)}>
            Go Back
          </button>
        </div>
      </div>
    </div>
  );
};

export default UploadResume;
