import React, { useState, useEffect } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "./ResumePreview.css";

const ResumePreview = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [resumeData, setResumeData] = useState(null);
  const [atsScore, setAtsScore] = useState(null);
  const [jobDescription, setJobDescription] = useState("");
  const [isAnalyzing, setIsAnalyzing] = useState(false);

  useEffect(() => {
    if (location.state?.resumeData) {
      setResumeData(location.state.resumeData);
    } else {
      navigate("/"); // Redirect to home if no resume data
    }
  }, [location, navigate]);

  const handleAtsAnalysis = async () => {
    if (!jobDescription.trim()) return;

    setIsAnalyzing(true);
    try {
      const response = await fetch("http://localhost:3001/api/score-resume", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ jobDescription }),
      });

      const data = await response.json();
      setAtsScore(data);
    } catch (error) {
      console.error("Error analyzing resume:", error);
    } finally {
      setIsAnalyzing(false);
    }
  };

  const handleDownloadPDF = async () => {
    try {
      const response = await fetch(
        "http://localhost:3001/api/generate-resume",
        {
          method: "POST",
        }
      );

      if (response.ok) {
        const blob = await response.blob();
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement("a");
        a.href = url;
        a.download = "resume.pdf";
        document.body.appendChild(a);
        a.click();
        window.URL.revokeObjectURL(url);
        document.body.removeChild(a);
      }
    } catch (error) {
      console.error("Error downloading PDF:", error);
    }
  };

  if (!resumeData) return null;

  return (
    <div className="resume-preview-container">
      <div className="resume-preview-header">
        <h1>Resume Preview</h1>
        <button className="download-button" onClick={handleDownloadPDF}>
          Download PDF
        </button>
      </div>

      <div className="resume-content">
        <div className="resume-preview">
          <div className="resume-header">
            <h2>{resumeData.name}</h2>
            <p className="contact-info">{resumeData.email}</p>
          </div>

          <div className="resume-section">
            <h3>Skills</h3>
            <div className="skills-list">
              {resumeData.skills.map((skill, index) => (
                <span key={index} className="skill-item">
                  {skill}
                </span>
              ))}
            </div>
          </div>

          <div className="resume-section">
            <h3>Work Experience</h3>
            <p>{resumeData.experience}</p>
          </div>

          <div className="resume-section">
            <h3>Education</h3>
            <p>{resumeData.education}</p>
          </div>
        </div>

        <div className="ats-analysis">
          <h2>ATS Analysis</h2>
          <div className="job-description-input">
            <textarea
              value={jobDescription}
              onChange={(e) => setJobDescription(e.target.value)}
              placeholder="Paste the job description here to analyze your resume..."
              rows={6}
            />
            <button
              className="analyze-button"
              onClick={handleAtsAnalysis}
              disabled={isAnalyzing || !jobDescription.trim()}
            >
              {isAnalyzing ? "Analyzing..." : "Analyze Resume"}
            </button>
          </div>

          {atsScore && (
            <div className="ats-results">
              <div className="score-card">
                <h3>Overall Score</h3>
                <div className="score-circle">
                  <span>{atsScore.score}%</span>
                </div>
              </div>

              <div className="score-details">
                <div className="score-item">
                  <h4>Keyword Match</h4>
                  <p>{atsScore.details.keywordMatch}%</p>
                </div>
                <div className="score-item">
                  <h4>Title Match</h4>
                  <p>{atsScore.details.titleMatch}%</p>
                </div>
              </div>

              <div className="matching-keywords">
                <h4>Matching Keywords</h4>
                <div className="keywords-list">
                  {atsScore.details.matchingKeywords.map((keyword, index) => (
                    <span key={index} className="keyword-tag">
                      {keyword}
                    </span>
                  ))}
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ResumePreview;
