import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import ModernTemplate from "./templates/ModernTemplate";
import ProfessionalTemplate from "./templates/ProfessionalTemplate";
import CreativeTemplate from "./templates/CreativeTemplate";
import "./ResumePreview.css";

const ResumePreview = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [resumeData] = useState(location.state?.resumeData || {});
  const [templateId] = useState(location.state?.templateId || "modern");
  const [jobDescription, setJobDescription] = useState("");
  const [atsScore, setAtsScore] = useState(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);

  if (!resumeData.name) {
    navigate("/");
    return null;
  }

  const handleAtsAnalysis = async () => {
    if (!jobDescription.trim()) {
      alert("Please enter a job description first");
      return;
    }

    setIsAnalyzing(true);
    try {
      const response = await fetch("http://localhost:3001/api/score-resume", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          resume: resumeData,
          jobDescription,
        }),
      });

      const data = await response.json();
      setAtsScore(data);
    } catch (error) {
      console.error("Error analyzing resume:", error);
      alert("Error analyzing resume. Please try again.");
    } finally {
      setIsAnalyzing(false);
    }
  };

  const handleDownload = async () => {
    try {
      const response = await fetch(
        "http://localhost:3001/api/generate-resume",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            ...resumeData,
            templateId,
          }),
        }
      );

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = `${resumeData.name.replace(/\s+/g, "_")}_Resume.pdf`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);
    } catch (error) {
      console.error("Error downloading resume:", error);
      alert("Error downloading resume. Please try again.");
    }
  };

  const renderTemplate = () => {
    switch (templateId) {
      case "modern":
        return <ModernTemplate resumeData={resumeData} />;
      case "professional":
        return <ProfessionalTemplate resumeData={resumeData} />;
      case "creative":
        return <CreativeTemplate resumeData={resumeData} />;
      default:
        return <ModernTemplate resumeData={resumeData} />;
    }
  };

  return (
    <div className="resume-preview-container">
      <div className="resume-preview-header">
        <h1>Resume Preview</h1>
        <div className="header-buttons">
          <button
            className="change-template-button"
            onClick={() => navigate("/select-template", { state: { resumeData } })}
          >
            Change Template
          </button>
          <button className="download-button" onClick={handleDownload}>
            Download PDF
          </button>
        </div>
      </div>

      <div className="resume-content">
        <div className="resume-preview">
          <div className="template-info">
            <span>
              Selected Template:{" "}
              {templateId.charAt(0).toUpperCase() + templateId.slice(1)}
            </span>
          </div>
          {renderTemplate()}
        </div>

        <div className="ats-analysis">
          <h2>ATS Analysis</h2>
          <div className="job-description-input">
            <textarea
              value={jobDescription}
              onChange={(e) => setJobDescription(e.target.value)}
              placeholder="Paste the job description here..."
              rows="6"
            />
            <button
              onClick={handleAtsAnalysis}
              disabled={isAnalyzing}
              className="analyze-button"
            >
              {isAnalyzing ? "Analyzing..." : "Analyze Resume"}
            </button>
          </div>

          {atsScore && (
            <div className="score-details">
              <div className="score-item">
                <h3>Overall Score</h3>
                <div className="score-value">{atsScore.overallScore}%</div>
              </div>
              <div className="score-item">
                <h3>Keyword Match</h3>
                <div className="score-value">{atsScore.keywordMatch}%</div>
              </div>
              <div className="score-item">
                <h3>Skills Match</h3>
                <div className="score-value">{atsScore.skillsMatch}%</div>
              </div>
              <div className="matching-keywords">
                <h3>Matching Keywords</h3>
                <ul>
                  {atsScore.matchingKeywords.map((keyword, index) => (
                    <li key={index}>{keyword}</li>
                  ))}
                </ul>
              </div>
              <div className="suggestions">
                <h3>Suggestions for Improvement</h3>
                <ul>
                  {atsScore.suggestions.map((suggestion, index) => (
                    <li key={index}>{suggestion}</li>
                  ))}
                </ul>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ResumePreview;
