import React, { useState, useRef, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { API_BASE_URL } from "../config";
import "./Chat.css";

const Chat = () => {
  const [messages, setMessages] = useState([
    {
      type: "bot",
      text: "Hello! I'll help you create your resume. What is your full name?",
    },
  ]);
  const [input, setInput] = useState("");
  const [currentStep, setCurrentStep] = useState("name");
  const [isComplete, setIsComplete] = useState(false);
  const [userData, setUserData] = useState({});
  const messagesEndRef = useRef(null);
  const navigate = useNavigate();

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleInputChange = (e) => {
    setInput(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    // Add user message
    setMessages((prev) => [...prev, { type: "user", text: input }]);

    try {
      const response = await fetch(`${API_BASE_URL}/api/chat`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          message: input,
          step: currentStep,
        }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();

      if (data.error) {
        setMessages((prev) => [
          ...prev,
          {
            type: "bot",
            text: "Sorry, something went wrong. Please try again.",
          },
        ]);
        return;
      }

      // Store user data
      const newUserData = { ...userData };
      switch (currentStep) {
        case "name":
          newUserData.name = input;
          break;
        case "email":
          newUserData.email = input;
          break;
        case "skills":
          newUserData.skills = input.split(",").map((skill) => skill.trim());
          break;
        case "experience":
          newUserData.experience = input;
          break;
        case "education":
          newUserData.education = input;
          break;
        default:
          console.log("Unknown step:", currentStep);
          break;
      }
      setUserData(newUserData);

      // Add bot response
      setMessages((prev) => [
        ...prev,
        { type: "bot", text: data.nextQuestion },
      ]);

      // Update step
      if (data.isComplete) {
        setIsComplete(true);
        // Navigate to template selection page with user data
        navigate("/select-template", { state: { resumeData: newUserData } });
      } else {
        const steps = ["name", "email", "skills", "experience", "education"];
        const currentIndex = steps.indexOf(currentStep);
        setCurrentStep(steps[currentIndex + 1]);
      }

      setInput("");
    } catch (error) {
      console.error("Error:", error);
      setMessages((prev) => [
        ...prev,
        {
          type: "bot",
          text: "Sorry, there was a network error. Please try again.",
        },
      ]);
    }
  };

  return (
    <div className="chat-container">
      <div className="messages">
        {messages.map((message, index) => (
          <div key={index} className={`message ${message.type}`}>
            <div className="message-content">{message.text}</div>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>
      {!isComplete && (
        <form onSubmit={handleSubmit} className="input-form">
          <div className="input-container">
            <input
              type="text"
              value={input}
              onChange={handleInputChange}
              placeholder="Type your answer..."
              className="chat-input"
            />
          </div>
          <button type="submit" className="send-button">
            Send
          </button>
        </form>
      )}
    </div>
  );
};

export default Chat;
