import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./HomePage.css";

const HomePage = () => {
  const navigate = useNavigate();
  const [resumeFile, setResumeFile] = useState(null);
  const [jobDescription, setJobDescription] = useState("");
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (
      file &&
      (file.type === "application/pdf" ||
        file.type ===
          "application/vnd.openxmlformats-officedocument.wordprocessingml.document")
    ) {
      setResumeFile(file);
      setError(null); // Clear any previous errors
    } else {
      setError("Please upload a PDF or DOCX file");
      setResumeFile(null);
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setError(null);

    if (!resumeFile) {
      setError("Please upload a resume file");
      return;
    }

    if (!jobDescription.trim()) {
      setError("Please provide a job description");
      return;
    }

    setLoading(true);
    try {
      const formData = new FormData();
      formData.append("resume", resumeFile);
      formData.append("jobDescription", jobDescription);

      const response = await fetch("http://localhost:3001/api/score-resume", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();

      // Validate the response data structure
      if (
        !data ||
        typeof data.overallScore !== "number" ||
        !Array.isArray(data.suggestions) ||
        !Array.isArray(data.matchingKeywords)
      ) {
        throw new Error("Invalid response format from server");
      }

      setAnalysis(data);
    } catch (error) {
      console.error("Error analyzing resume:", error);
      setError(error.message || "Error analyzing resume. Please try again.");
      setAnalysis(null);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="homepage-container">
      <div className="homepage-content">
        <div className="hero-section">
          <h1 className="gradient-text">Create Your Professional Resume</h1>
          <p className="hero-subtitle">
            Powered by AI, designed for success. Build a standout resume in
            minutes.
          </p>
          <div className="features-grid">
            <div className="feature-card">
              <div className="feature-icon">
                <svg viewBox="0 0 24 24" fill="currentColor">
                  <path d="M19 3H5c-1.1 0-2 .9-2 2v14c0 1.1.9 2 2 2h14c1.1 0 2-.9 2-2V5c0-1.1-.9-2-2-2zm-5 14H7v-2h7v2zm3-4H7v-2h10v2zm0-4H7V7h10v2z" />
                </svg>
              </div>
              <h3>Smart Templates</h3>
              <p>
                Choose from professionally designed templates optimized for ATS
              </p>
            </div>
            <div className="feature-card">
              <div className="feature-icon">
                <svg viewBox="0 0 24 24" fill="currentColor">
                  <path d="M21 11.5a8.38 8.38 0 01-.9 3.8 8.5 8.5 0 01-7.6 4.7 8.38 8.38 0 01-3.8-.9L3 21l1.9-5.7a8.38 8.38 0 01-.9-3.8 8.5 8.5 0 014.7-7.6 8.38 8.38 0 013.8-.9h.5a8.48 8.48 0 018 8v.5z" />
                </svg>
              </div>
              <h3>AI Chat Assistant</h3>
              <p>Get guided assistance to craft the perfect resume content</p>
            </div>
            <div className="feature-card">
              <div className="feature-icon">
                <svg viewBox="0 0 24 24" fill="currentColor">
                  <path d="M12 22s8-4 8-10V5l-8-3-8 3v7c0 6 8 10 8 10z" />
                </svg>
              </div>
              <h3>ATS Optimized</h3>
              <p>Ensure your resume passes Applicant Tracking Systems</p>
            </div>
          </div>
          <button className="start-button" onClick={() => navigate("/chat")}>
            <span className="button-text">Start Creating</span>
            <span className="button-icon">
              <svg
                viewBox="0 0 24 24"
                fill="none"
                stroke="currentColor"
                strokeWidth="2"
              >
                <path d="M5 12h14M12 5l7 7-7 7" />
              </svg>
            </span>
          </button>
        </div>

        <div className="ats-analysis-section">
          <h2>Check Your Resume's ATS Score</h2>
          {error && <div className="error-message">{error}</div>}
          <form onSubmit={handleSubmit} className="ats-form">
            <div className="upload-section">
              <label htmlFor="resume-upload" className="upload-label">
                <div className="upload-box">
                  <i className="fas fa-cloud-upload-alt"></i>
                  <p>Upload Resume (PDF/DOCX)</p>
                  {resumeFile && <p className="file-name">{resumeFile.name}</p>}
                </div>
              </label>
              <input
                id="resume-upload"
                type="file"
                accept=".pdf,.docx"
                onChange={handleFileChange}
                className="file-input"
              />
            </div>

            <div className="job-description-section">
              <label htmlFor="job-description">Job Description</label>
              <textarea
                id="job-description"
                value={jobDescription}
                onChange={(e) => {
                  setJobDescription(e.target.value);
                  setError(null);
                }}
                placeholder="Paste the job description here..."
                rows="6"
              />
            </div>

            <button type="submit" className="analyze-button" disabled={loading}>
              {loading ? "Analyzing..." : "Analyze Resume"}
            </button>
          </form>

          {analysis && (
            <div className="results-section">
              <div className="score-circle">
                <span className="score">{analysis.overallScore}%</span>
                <span className="score-label">ATS Score</span>
              </div>
              <div className="feedback-list">
                <h3>Analysis Feedback</h3>
                <ul>
                  {analysis.suggestions.map((suggestion, index) => (
                    <li key={index}>{suggestion}</li>
                  ))}
                </ul>
              </div>
              <div className="matching-keywords">
                <h3>Matching Keywords</h3>
                <ul>
                  {analysis.matchingKeywords.map((keyword, index) => (
                    <li key={index}>{keyword}</li>
                  ))}
                </ul>
              </div>
            </div>
          )}
        </div>
      </div>
      <div className="background-gradient"></div>
      <div className="background-pattern"></div>
    </div>
  );
};

export default HomePage;
