import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import "./ATSAnalysis.css";

const ATSAnalysis = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [resumeFile, setResumeFile] = useState(null);
  const [jobDescription, setJobDescription] = useState("");
  const [analysis, setAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);

  if (!location.state?.resumeData.personalInfo?.fullName) {
    navigate("/");
    return null;
  }

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (
      file &&
      (file.type === "application/pdf" ||
        file.type ===
          "application/vnd.openxmlformats-officedocument.wordprocessingml.document")
    ) {
      setResumeFile(file);
    } else {
      alert("Please upload a PDF or DOCX file");
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (!resumeFile || !jobDescription) {
      alert("Please upload a resume and provide a job description");
      return;
    }

    setLoading(true);
    // TODO: Implement API call to analyze resume
    // For now, we'll simulate a response
    setTimeout(() => {
      setAnalysis({
        score: 85,
        feedback: [
          "Your resume matches 85% of the job requirements",
          "Strong alignment with technical skills",
          "Consider adding more specific achievements",
          "Keywords found: 15/20",
        ],
      });
      setLoading(false);
    }, 2000);
  };

  const handleBack = () => {
    navigate("/resume-preview", {
      state: {
        resumeData: location.state?.resumeData,
        templateId: location.state?.templateId,
      },
    });
  };

  return (
    <div className="ats-container">
      <h2>Resume ATS Analysis</h2>
      <form onSubmit={handleSubmit} className="ats-form">
        <div className="upload-section">
          <label htmlFor="resume-upload" className="upload-label">
            <div className="upload-box">
              <i className="fas fa-cloud-upload-alt"></i>
              <p>Upload Resume (PDF/DOCX)</p>
              {resumeFile && <p className="file-name">{resumeFile.name}</p>}
            </div>
          </label>
          <input
            id="resume-upload"
            type="file"
            accept=".pdf,.docx"
            onChange={handleFileChange}
            className="file-input"
          />
        </div>

        <div className="job-description-section">
          <label htmlFor="job-description">Job Description</label>
          <textarea
            id="job-description"
            value={jobDescription}
            onChange={(e) => setJobDescription(e.target.value)}
            placeholder="Paste the job description here..."
            rows="6"
          />
        </div>

        <button type="submit" className="analyze-button" disabled={loading}>
          {loading ? "Analyzing..." : "Analyze Resume"}
        </button>
      </form>

      {analysis && (
        <div className="results-section">
          <div className="score-circle">
            <span className="score">{analysis.score}%</span>
            <span className="score-label">ATS Score</span>
          </div>
          <div className="feedback-list">
            <h3>Analysis Feedback</h3>
            <ul>
              {analysis.feedback.map((item, index) => (
                <li key={index}>{item}</li>
              ))}
            </ul>
          </div>
        </div>
      )}
    </div>
  );
};

export default ATSAnalysis;
