import React, { useState } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import ModernTemplate from "./templates/ModernTemplate";
import ProfessionalTemplate from "./templates/ProfessionalTemplate";
import CreativeTemplate from "./templates/CreativeTemplate";
import { API_BASE_URL } from "../config";
import "./ResumePreview.css";

const ResumePreview = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const [resumeData] = useState(location.state?.resumeData || {});
  const [templateId] = useState(location.state?.templateId || "modern");
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  if (!resumeData.name) {
    navigate("/");
    return null;
  }

  const handleChangeTemplate = () => {
    navigate("/select-template", { state: { resumeData } });
  };

  const handleAtsAnalysis = () => {
    navigate("/ats-analysis", { state: { resumeData, templateId } });
  };

  const handleDownload = async () => {
    setIsLoading(true);
    setError(null);

    try {
      const response = await fetch(`${API_BASE_URL}/api/generate-resume`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ resumeData, templateId }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = `${resumeData.basics.name.replace(/\s+/g, "_")}_resume.pdf`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);
    } catch (error) {
      console.error("Error generating PDF:", error);
      setError("Failed to generate PDF. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  const getTemplateComponent = () => {
    switch (templateId) {
      case "modern":
        return <ModernTemplate resumeData={resumeData} />;
      case "professional":
        return <ProfessionalTemplate resumeData={resumeData} />;
      case "creative":
        return <CreativeTemplate resumeData={resumeData} />;
      default:
        return <ModernTemplate resumeData={resumeData} />;
    }
  };

  return (
    <div className="resume-preview-container">
      <div className="resume-preview-header">
        <h1>Resume Preview</h1>
        <div className="header-buttons">
          <button className="analyze-button" onClick={handleAtsAnalysis}>
            <span className="button-text">ATS Analysis</span>
            <span className="button-icon">
              <svg
                viewBox="0 0 24 24"
                fill="none"
                stroke="currentColor"
                strokeWidth="2"
              >
                <path d="M5 12h14M12 5l7 7-7 7" />
              </svg>
            </span>
          </button>
          <button className="download-button" onClick={handleDownload}>
            Download PDF
          </button>
          <button
            className="change-template-button"
            onClick={handleChangeTemplate}
          >
            Change Template
          </button>
        </div>
      </div>

      <div className="resume-content">
        <div className="resume-preview">
          <div className="template-info">
            {templateId.charAt(0).toUpperCase() + templateId.slice(1)} Template
          </div>
          {getTemplateComponent()}
        </div>
      </div>
    </div>
  );
};

export default ResumePreview;
