import React from "react";
import { useNavigate, useLocation } from "react-router-dom";
import ModernTemplate from "./templates/ModernTemplate";
import ProfessionalTemplate from "./templates/ProfessionalTemplate";
import CreativeTemplate from "./templates/CreativeTemplate";
import "./TemplateSelection.css";

const templates = [
  {
    id: "modern",
    name: "Modern Template",
    description: "A clean and contemporary design with a focus on readability",
    component: ModernTemplate,
  },
  {
    id: "professional",
    name: "Professional Template",
    description: "Traditional layout perfect for corporate positions",
    component: ProfessionalTemplate,
  },
  {
    id: "creative",
    name: "Creative Template",
    description: "Stand out with a unique design for creative roles",
    component: CreativeTemplate,
  },
];

const TemplateSelection = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const resumeData = location.state?.resumeData;

  const handleTemplateSelect = (templateId) => {
    if (!resumeData) {
      navigate("/");
      return;
    }

    navigate("/resume-preview", {
      state: {
        resumeData,
        templateId,
      },
    });
  };

  if (!resumeData) {
    return (
      <div className="template-selection-container">
        <h2>Please complete your resume information first</h2>
        <button onClick={() => navigate("/")}>Go Back</button>
      </div>
    );
  }

  return (
    <div className="template-selection-container">
      <h2>Select Your Resume Template</h2>
      <div className="templates-grid">
        {templates.map((template) => {
          const TemplateComponent = template.component;
          return (
            <div
              key={template.id}
              className="template-card"
              onClick={() => handleTemplateSelect(template.id)}
            >
              <div className="template-preview">
                <TemplateComponent resumeData={{}} isPreview={true} />
              </div>
              <h3>{template.name}</h3>
              <p>{template.description}</p>
              <button className="select-button">Select Template</button>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default TemplateSelection;
